const axios = require('axios');

async function execute() {
    const cpfCnpj = $json.mensagem; // Recebe o CPF do cliente

    // Primeira Requisição - ListarClientes
    try {
        const response1 = await axios.get(`https://sandbox.asaas.com/api/v3/customers`, {
            params: {
                cpfCnpj: cpfCnpj
            },
            headers: {
                'accept': 'application/json',
                'access_token': '$aact_YTU5YTE0M2M2N2I4MTliNzk0YTI5N2U5MzdjNWZmNDQ6OjAwMDAwMDAwMDAwMDAwODc4MTU6OiRhYWNoX2ZmNGUxZWNhLWUxMTgtNDlkNC1iZGYxLThhYTM0NGM5ODRlMw=='
            }
        });

        const data1 = response1.data;

        if (!data1 || !data1.data || data1.data.length === 0) {
            throw new Error('Cliente não encontrado.');
        }

        const customerID = data1.data[0].id; // Extrai o ID do cliente

        // Segunda Requisição - ListarBoletosAsaas
        const response2 = await axios.get(`https://sandbox.asaas.com/api/v3/payments`, {
            params: {
                customer: customerID,
                status: 'PENDING'
            },
            headers: {
                'accept': 'application/json',
                'access_token': '$aact_YTU5YTE0M2M2N2I4MTliNzk0YTI5N2U5MzdjNWZmNDQ6OjAwMDAwMDAwMDAwMDAwODc4MTU6OiRhYWNoX2ZmNGUxZWNhLWUxMTgtNDlkNC1iZGYxLThhYTM0NGM5ODRlMw=='
            }
        });

        const data2 = response2.data;

        if (!data2 || !data2.data || data2.data.length === 0) {
            throw new Error('Nenhum pagamento pendente encontrado.');
        }

        const id_payment_pending = data2.data[0].id; // Extrai o ID do pagamento pendente

        // Terceira Requisição - Obter o QR Code PIX
        const response3 = await axios.get(`https://sandbox.asaas.com/api/v3/payments/${id_payment_pending}/pixQrCode`, {
            headers: {
                'accept': 'application/json',
                'access_token': '$aact_YTU5YTE0M2M2N2I4MTliNzk0YTI5N2U5MzdjNWZmNDQ6OjAwMDAwMDAwMDAwMDAwODc4MTU6OiRhYWNoX2ZmNGUxZWNhLWUxMTgtNDlkNC1iZGYxLThhYTM0NGM5ODRlMw=='
            }
        });

        const pixQrCodeData = response3.data;

        // Converte o resultado das requisições para uma string JSON
        const resultString = JSON.stringify({
            boletos: data2,
            pixQrCode: pixQrCodeData
        });

        return resultString;

    } catch (error) {
        return `Error: ${error.message}`;
    }
}

